import React from 'react';
import ReactDOM from 'react-dom';
import CustomButton from './customButton';
import './index.css';


class PotType extends React.Component {

	constructor() {
		super();

		this.state = {
			submitValue : ''
		}
	}

	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    return(this.state.submitValue);
  	};


  	// each map is going to look something like this: 

	hiNupurILoveU(ev, mappy) {
		for (var key in mappy) {
			// console.log('button: ', key)

			let button = mappy[key]
			if (key == ev.currentTarget.value) {
				button.clickedOn()
		 	} else {
		 		button.clickedOff()
		 	}
		}
		// might need to pass in state?? 
		this.state.submitValue = ev.currentTarget.value

	}

	handleClick = ev => { 
		let mappy = {"RFI": this._custombutton, "Vs Open": this._custombutton2, "Vs 3":  this._custombutton3, "BVB": this._custombutton4}

		this.hiNupurILoveU(ev, mappy)
