import React from 'react';
//import ReactDOM from 'react-dom';
import CustomButton from './customButton';
import './index.css';
import * as constant from './stringConstants';


class Hero extends React.Component {

	mappy = {[constant.UTG]: "hello", [constant.MP]: this._custombutton2, [constant.LJ]:  this._custombutton3, [constant.HJ]: this._custombutton4, 
			[constant.CO]: this._custombutton5, [constant.BU]: this._custombutton6, [constant.SB]:  this._custombutton7, [constant.BB]: this._custombutton8}

	constructor() {
		super();

		// submitValue holds the value of clicked button in villain row
		this.state = {
			submitValue : ''
		}
	}


	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    console.log(this.state.submitValue);
  	};


	handleClick = ev => { 
		console.log("MAPPPPPPPPY: ", this.mappy)
		this.props.vToggleButtons(ev, this.mappy)

		// update state of value in parents class bumfuck
		this.props.updateVillainState(ev.currentTarget.value)
		// update state of submitValue in this class (for use in form submission)
		this.setState ({submitValue: ev.currentTarget.value})
		//this.props.vSendData(this.state.submitValue)
	}


	componentDidUpdate(props) {
		// mappy is a dict of hero row buttons 
		mappy = {[constant.UTG]: this._custombutton, [constant.MP]: this._custombutton2, [constant.LJ]:  this._custombutton3, [constant.HJ]: this._custombutton4, 
			[constant.CO]: this._custombutton5, [constant.BU]: this._custombutton6, [constant.SB]:  this._custombutton7, [constant.BB]: this._custombutton8}

		// listButtons is an array of BvB row buttons 
		let allButtons = [this._custombutton, this._custombutton2, this._custombutton3, this._custombutton4, 
			this._custombutton5, this._custombutton6, this._custombutton7, this._custombutton8]
		let listButtonsVsopen = allButtons - this._customButton7 - this._customButton8
		// [this._custombutton, this._custombutton2, this._custombutton3, this._custombutton4, 
		// 	this._custombutton5, this._custombutton6]
		let listButtonsVs3 = [this._custombutton2, this._custombutton3, this._custombutton4, 
			this._custombutton5, this._custombutton6, this._custombutton7, this._custombutton8]

		// loop through each button and disable or enable based on what PotType value is
		if ((this.props.potValue === constant.RFI) || (this.props.potValue === constant.BVB)) {
			for (let butt = 0; butt < allButtons.length; butt++) {
				let button = allButtons[butt]
				// disable button
				button.disabled()
				// unclick button
				button.clickedOff()
			}
		}
		else if (this.props.potValue === constant.VS_OPEN) {
			// initial grey out
			// disable and unclick last two buttons when vs open clicked
			this._custombutton7.clickedOff()
			this._custombutton7.disabled()
			this._custombutton8.clickedOff()
			this._custombutton8.disabled()
			// enable all other buttons 
			for (let butt = 0; butt < listButtonsVsopen.length; butt++) {
				let button = listButtonsVsopen[butt]
				button.enabled()
			}

			// grey out based on VP<HP rule
			// check if hero button has been selected
			if (this.props.heroValue !== '') {
				// find what position clicked hero button is at in array of buttons
				console.log("HERO VALUE ", this.props.heroValue)
				var hpIndex = allButtons.indexOf(this.props.heroValue);
				// click off and disable all villain buttons before that hero index
				for (let butt = 0; butt < hpIndex; butt++) {
					let button = allButtons[butt]
					button.clickedOff()
					button.disable()
				}
				// enable all villain buttons after that hero index
				for (let butt = hpIndex; butt < listButtons.length; butt++) {
					console.log("INDEX: ", hpIndex)
					// let button = listButtons[butt]
					// button.enabled()
				}
			}

		}
		else if (this.props.potValue === constant.VS_3) {
			// disable and unclick first button when vs open clicked
			this._custombutton.clickedOff()
			this._custombutton.disabled()
			// enable all other buttons 
			for (let butt = 0; butt < listButtonsVs3.length; butt++) {
				let button = listButtonsVs3[butt]
				button.enabled()
			}
		}
	}

	// <input type='submit' />

	render() {

	    return (
	      	<div>
	       		<form onSubmit={this.onFormSubmit}>
	        		<div className="board-row">
			        	<CustomButton value={constant.UTG} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton = ref)} ></CustomButton>
			       		<CustomButton value={constant.MP} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton2 = ref)} ></CustomButton>
			       		<CustomButton value={constant.LJ} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton3 = ref)} ></CustomButton>
			       		<CustomButton value={constant.HJ} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton4 = ref)} ></CustomButton>
			       		<CustomButton value={constant.CO} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton5 = ref)} ></CustomButton>
			       		<CustomButton value={constant.BU} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton6 = ref)} ></CustomButton>
			       		<CustomButton value={constant.SB} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton7 = ref)} ></CustomButton>
			       		<CustomButton value={constant.BB} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton8 = ref)} ></CustomButton>
	        		</div>
	        	</form>
	      	</div>
	    );
	}
}


export default Hero;
