import './index.css';
import TextField from '@material-ui/core/TextField';


class HoleInput extends React.Component {

	constructor() {
		super();

		this.state = {
			holeInputValue: "",
         errors: {}
		}
	}

	handleValidation(){
		let errors = {};
		let formIsValid = true;

		//Hole Input
		if(!fields["hole"]){
		   formIsValid = false;
		   errors["hole"] = "Hole input cannot be empty";
		}

		if (typeof fields["hole"] !== "undefined") {
		   if (!fields["hole"].match(/^([AKQJakqj2-9]|10)([AKQJakqj2-9]|10)([osOS])$/)){
		      formIsValid = false;
		      errors["hole"] = "Wrong bro";
		   }
		}

		this.setState({errors: errors});
		return formIsValid;
	}


  	_handleTextFieldChange = (ev) => {
  		this.setState({ holeInputValue: ev.target.value });

		if (this.handleValidation()) {

		}
		else {
			alert("Form has errors.")
		}

      // update state of value in parents class bumfuck
		this.props.updateHoleState(ev.currentTarget.value)
  	}


	render() {
	    return (
	    	<div className = "holeinput">
	    	    <form noValidate autoComplete="off">
     				 <TextField
						ref="hole"
						label="hole input"
						id="standard-size-small"
						defaultValue=""
						size="small"
						required
						errorText={this.state.errors}
						onChange={(ev)=>this._handleTextFieldChange(ev)}
					 />
    			</form>
    		</div>
	    );
  		}
}


export default HoleInput;
