import CustomButton from './customButton';
import './index.css';
// import * as constClass from './stringConstants';
import {RFI, VS_OPEN, VS_3} from './stringConstants';
import {SB_U, SB_L_v_I, SB_v_3, BB_v_L, BB_v_O} from './stringConstants';


class BvB extends React.Component {

	constructor() {
		super();

		this.state = {
			submitValue : ''
		}
	}

	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    console.log(this.state.submitValue);
  	};

	handleClick = ev => { 
		let mappy = {SB_U: this._custombutton, SB_L_v_I: this._custombutton2, SB_v_3:  this._custombutton3, 
			BB_v_L: this._custombutton4, BB_v_O: this._custombutton5}
		this.props.bButtonState(ev, mappy)

		this.setState ({submitValue: ev.currentTarget.value})
		this.props.bSendData(this.state.submitValue)


		// if (ev.currentTarget.value === 'SB Unopened') {
		// 	this._custombutton.clickedOn()
		// 	this._custombutton2.clickedOff()
		// 	this._custombutton3.clickedOff()
		// 	this._custombutton4.clickedOff()
		// 	this._custombutton5.clickedOff()
		// 	this.state.submitValue = 'SB Unopened'
		// }
		// else if (ev.currentTarget.value === 'SB Limp v ISO') {
		// 	this._custombutton.clickedOff()
		// 	this._custombutton2.clickedOn()
		// 	this._custombutton3.clickedOff()
		// 	this._custombutton4.clickedOff()
		// 	this._custombutton5.clickedOff()
		// 	this.state.submitValue = 'SB Limp v ISO'
		// }
		// else if (ev.currentTarget.value === 'SB v 3!') {
		// 	this._custombutton.clickedOff()
		// 	this._custombutton2.clickedOff()
		// 	this._custombutton3.clickedOn()
		// 	this._custombutton4.clickedOff()
		// 	this._custombutton5.clickedOff()
		// 	this.state.submitValue = 'SB v 3!'
		// }
		// else if (ev.currentTarget.value === 'BB v Limp') {
		// 	this._custombutton.clickedOff()
		// 	this._custombutton2.clickedOff()
		// 	this._custombutton3.clickedOff()
		// 	this._custombutton4.clickedOn()
		// 	this._custombutton5.clickedOff()
		// 	this.state.submitValue = 'BB v Limp'
		// }
		// else {
		// 	this._custombutton.clickedOff()
		// 	this._custombutton2.clickedOff()
		// 	this._custombutton3.clickedOff()
		// 	this._custombutton4.clickedOff()
		// 	this._custombutton5.clickedOn()
		// 	this.state.submitValue = ''
		// }
	}

	// disable BvB buttons when PotType is chosen
	disableButtons() {




	// 	for (let butt = 0; butt < listButtons.length; butt++) {
	// 			listButtons[butt].disabled()
	// 		}
	// 	if (this.props.result === BVB) {
	// 		for (let button = 0; button < listButtons.length; button++) {
	// 	 		console.log(listButtons[button])
	// 	// 		this.listButtons[button].clickedOn()
	// 	}
	// }

		// listButtons is an array of BvB buttons defined in the constructor
		let listButtons = [this._custombutton, this._custombutton2, this._custombutton3, this._custombutton4, this._custombutton5]

		// loop through each button and disable or enable based on what clicked PotType value is
		if ((this.props.result === RFI) || (this.props.result === VS_OPEN) || (this.props.result === VS_3)) {

			for (let butt = 0; butt < listButtons.length; butt++) {
				listButtons[butt].this.disable()
			}
		}
		// else {
		// 	for (let button = 0; button < this.listButtons.length; button++) {
		// 		console.log(this.listButtons[button])
		// 		this.listButtons[button].clickedOn()
		// 		//listButtons[button].enabled()
		// 	}
		// }
	}


	render() {
		this.disableButtons()

	    return (
	      	<div>
	       		<form onSubmit={this.onFormSubmit}>
	        		<div className="board-row">
	        			<CustomButton value={SB_U} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton = ref)} ></CustomButton>
	       				<CustomButton value={SB_L_v_I} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton2 = ref)} ></CustomButton>
	       				<CustomButton value={SB_v_3} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton3 = ref)} ></CustomButton>
	       				<CustomButton value={BB_v_L} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton4 = ref)} ></CustomButton>
	       				<CustomButton value={BB_v_O} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton5 = ref)} ></CustomButton>
	        		</div>
	        		<input type='submit' />
	        	</form>
	      </div>
	    );
  	}
}


export default BvB;
