import React from 'react';
import ReactDOM from 'react-dom';
import CustomButton from './customButton';


class BvB extends React.Component {

	constructor() {
		super();

		this.state = {
			submitValue : ''
		}
	}

	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    console.log(this.state.submitValue);
  	};

	handleClick = ev => { 
		if (ev.currentTarget.value === 'SB U') {
			this._custombutton.clickedOn()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this.state.submitValue = 'SB U'
		}
		else if (ev.currentTarget.value === 'SB LvI') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOn()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this.state.submitValue = 'SB LvI'
		}
		else if (ev.currentTarget.value === 'SB v 3') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOn()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this.state.submitValue = 'SB v 3'
		}
		else if (ev.currentTarget.value === 'BBvL') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOn()
			this._custombutton5.clickedOff()
			this.state.submitValue = 'BBvL'
		}
		else {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOn()
			this.state.submitValue = 'BvB'
		}

	}

	render() {

	    return (
	      	<div>
	       		<form onSubmit={this.onFormSubmit}>
	        		<div className="board-row">
	        			<CustomButton value='SB U' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton = ref)} ></CustomButton>
	       				<CustomButton value='SB LvI' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton2 = ref)} ></CustomButton>
	       				<CustomButton value='SBv3' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton3 = ref)} ></CustomButton>
	       				<CustomButton value='BBvL' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton4 = ref)} ></CustomButton>
	       				<CustomButton value='BBvO' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton5 = ref)} ></CustomButton>
	        		</div>
	        		<input type='submit' />
	        	</form>
	      </div>
	    );
  	}
}


export default BvB;
