import React from 'react';
import ReactDOM from 'react-dom';
import Button from '@material-ui/core/Button';
import { createMuiTheme, MuiThemeProvider } from '@material-ui/core/styles';
//import createMuiTheme from 'material-ui/styles/theme'
import {pink, lightBlue} from '@material-ui/core/colors'
// import createPalette from '@material-ui/core/palette'
import PotType from './potType';
import StackDepth from './stackDepth';
import Hero from './hero';
import Villain from './villain';



class Button1 extends React.Component {


	muiTheme = createMuiTheme({ palette: { primary: lightBlue, secondary: pink } })

	constructor(){
        super();

        this.state = {
        	color: 'lightBlue',
        	clicked: false,
        	button1: ''
        }
    }


	handleClick = ev => { 
		if (this.state.clicked === false) {
			this.setState({color: 'pink'}) 
			this.setState({clicked: true})
			this.setState({button1: ev.currentTarget.value})

			console.log('RFI')
		}
		if (this.state.clicked === true) {
			this.setState({color: 'lightBlue'}) 
			this.setState({clicked: false})
			this.setState({button1: ''})

			console.log('')
		}

	}


	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    console.log(this.state);
  	};


	render() {
		return (
	      	<div>


		        <form onSubmit={this.onFormSubmit}>

		        	<Button
		        		variant="contained" 
		          		name='button1'
		         		value='RFI'
		         		style={{backgroundColor: this.state.color}}
		         		onClick={(ev) => this.handleClick(ev)}
	          		>
		          		RFI
		        	</Button>


		          	<input
		            	placeholder='Hole Card'
		            	ref='holeCard'
		          	/>

		          	<input type='submit' />
		        </form>
	      	</div>
	    );

	 }

	}



class Button2 extends React.Component {


	muiTheme = createMuiTheme({ palette: { primary: lightBlue, secondary: pink } })

	constructor(){
        super();

        this.state = {
        	color: 'lightBlue',
        	clicked: false,
        	button1: ''
        }
    }


	handleClick = ev => { 
		if (this.state.clicked === false) {
			this.setState({color: 'pink'}) 
			this.setState({clicked: true})
			this.setState({button1: ev.currentTarget.value})

			console.log('Vs Open')
		}
		if (this.state.clicked === true) {
			this.setState({color: 'lightBlue'}) 
			this.setState({clicked: false})
			this.setState({button1: ''})

			console.log('')
		}

	}


	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    console.log(this.state);
  	};


	render() {
		return (
	      	<div>
		        <form onSubmit={this.onFormSubmit}>

			        <Button
			        	variant="contained"
						name='button-2'
						value='Vs Open'
						style={{backgroundColor: this.state.color}}
		         		onClick={(ev) => this.handleClick(ev)}
			        >
			        	Vs Open
		        	</Button>


		          	<input
		            	placeholder='Hole Card'
		            	ref='holeCard'
		          	/>

		          	<input type='submit' />
		        </form>
	      	</div>
	    );

	 }

}


