import React from 'react';
import ReactDOM from 'react-dom';
import Button from '@material-ui/core/Button';

import PotType from './pottype';
import StackDepth from './stackDepth';
import Hero from './hero';
import Villain from './villain';
import BvB from './bvb';


export default class BumFuck extends React.Component {

	constructor() {
        super();

        this.state = {
        	value: '',    
        }

        this.getData = this.getData.bind(this);   
    }


    getData(val) {
    	this.setState ({
    		value: val
    	})
    	console.log("val", this.state.value)   	
	}

	// each map is going to look something like this: 
	buttonState(e, mappy) {
		for (var key in mappy) {

			let button = mappy[key]
			if (key == ev.currentTarget.value) {
				button.clickedOn()
		 	} else {
		 		button.clickedOff()
		 	}
		}	
	}

	render() {
		return (
			<div>
				<PotType pButtonState={this.buttonState} sendData={this.getData}/>
				<StackDepth result = {this.state.value}/>
				<Hero result = {this.state.value}/>
				<Villain result = {this.state.value}/>
				<BvB result = {this.state.value}/>
			</div>
		);
	}
}
