import React from 'react';
//import ReactDOM from 'react-dom';
//import Button from '@material-ui/core/Button';

import PotType from './pottype';
import StackDepth from './stackDepth';
import Hero from './hero';
import Villain from './villain';
import BvB from './bvb';


export default class BumFuck extends React.Component {

	constructor() {
        super();

        // submitValue holds the value of clicked button in pottype row
        this.state = {
        	potValue: '',    
        }

        this.updatePotValue = this.updatePotValue.bind(this);   
    }

    // setState forces rerender so buttons update with each click

    updatePotValue(val) {
    	this.setState ({potValue: val})	
	}

	// helper function called in every class's handleClick, allowing user to toggle between buttons 
	// mappy = map of values to buttons (defined in handleclick)
	// ev = event state 
	setButton(ev, mappy) {
		for (var key in mappy) {
			let button = mappy[key]
			//console.log("VALUE: ", ev.currentTarget.value)
			//console.log("KEY: ", key)
			if (key === ev.currentTarget.value) {
				//console.log("BUTTON: ", mappy[key])
				button.clickedOn()
		 	} 
		 	else {
		 		button.clickedOff()
		 	}
		}	
	}



	render() {
		return (
			// potValue = {this.state.value} is passing in the value of the selected potType button
			<div>
				<PotType pUpdateButtonState={this.updateButtonStates} updatePottypeState={this.updatePotValue}/>
				<StackDepth sUpdateButtonState={this.updateButtonStates}  potValue={this.state.potValue}/>
				<Hero hUpdateButtonState={this.updateButtonStates}  potValue={this.state.potValue}/>
				<Villain vUpdateButtonState={this.updateButtonStates}  potValue={this.state.potValue}/>
				<BvB bUpdateButton={this.updateButtonStates}  potValue={this.state.potValue}/>
			</div>
		);
	}
}
