import React from 'react';
//import ReactDOM from 'react-dom';
//import CustomButton from './customButton';
import './index.css';
import TextField from '@material-ui/core/TextField';


class HoleInput extends React.Component {

	constructor() {
		super();

		this.state = {
			holeInputValue: "",
         error: ""
		}
	}


  	_handleTextFieldChange = (ev) => {
  		this.setState({ holeInputValue: ev.target.value });

		let validHole = "false";

		if (!ev.target.value.match(/^([AKQJakqj2-9]|10)([AKQJakqj2-9]|10)([osOS])$/)) {
			this.setState({error: "Wrong bro"})
		}
		else {
			this.setState({error: ""})
			validHole = "true";
		}

      // update state of value in parents class bumfuck
		this.props.validHoleInput(validHole)
		this.props.updateHoleState(ev.currentTarget.value)
  	}


	helphelp() {
		this.setState({ error: "Nope"});
	}


	render() {
	    return (
	    	<div className = "holeinput">
	    	    <form noValidate autoComplete="off">
     				 <TextField
						label="hole input"
						id="standard-size-small"
						defaultValue=""
						size="small"
						onChange={(ev)=>this._handleTextFieldChange(ev)}
						helperText={this.state.error}
					 />
    			</form>
    		</div>
	    );
  		}
}


export default HoleInput;
