import React from 'react';
import ReactDOM from 'react-dom';
import Button from '@material-ui/core/Button';

import PotType from './pottype';
import StackDepth from './stackDepth';
import Hero from './hero';
import Villain from './villain';
import BvB from './bvb';
import {RFI, VS_OPEN, VS_3, BVB} from './stringConstants';


export default class BumFuck extends React.Component {

	constructor() {
        super();

        this.state = {
        	value: '',    
        }

        this.getData = this.getData.bind(this);   
    }

    // setState forces rerender so buttons update with each click
    getData(val) {
    	this.setState ({
    		value: val
    	})	
	}

	// helper function called in every class's handleClick, allowing user to toggle between buttons 
	// mappy = map of values to buttons (defined in handleclick)
	// ev = event state 
	buttonState(ev, mappy) {
		for (var key in mappy) {
			let button = mappy[key]
			if (key == ev.currentTarget.value) {
				button.clickedOn()
		 	} else {
		 		button.clickedOff()
		 	}
		}	
	}

	disabedOrNah(listButtons) {
		console.log('YUH', this.state.value)
		// if ((this.state.value === RFI) || (this.state.value === VS_OPEN) || (this.state.value === VS_3)) {
		// 	for (var button in listButtons) {
		// 		console.log('BUTTON: ', button)
		// 		button.disabled()
		// 	}
		// }
		// else {
		// 	for (var button in listButtons) {
		// 		button.enabled()
		// 	}
		// }
	}


	render() {
		return (
			<div>
				<PotType pButtonState={this.buttonState} sendData={this.getData}/>
				<StackDepth result = {this.state.value}/>
				<Hero hButtonState={this.buttonState} result = {this.state.value}/>
				<Villain vButtonState={this.buttonState} result = {this.state.value}/>
				<BvB bDisabledOrNah={this.disabedOrNah} result = {this.state.value}/>
			</div>
		);
	}
}
