import React from 'react';
import ReactDOM from 'react-dom';
import CustomButton from './customButton';
import './index.css';


class PotType extends React.Component {

	constructor() {
		super();

		this.state = {
			submitValue : ''
		}
	}

	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    return(this.state.submitValue);
  	};

	handleClick = ev => { 
		if (ev.currentTarget.value === 'RFI') {
			this._custombutton.clickedOn()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this.state.submitValue = 'RFI'
			////kick current clicked value out to blah
		}
		else if (ev.currentTarget.value === 'Vs Open') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOn()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this.state.submitValue = 'Vs Open'
		}
		else if (ev.currentTarget.value === 'Vs 3') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOn()
			this._custombutton4.clickedOff()
			this.state.submitValue = 'Vs 3'
		}
		else {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOn()
			this.state.submitValue = 'BvB'
		}
		this.props.sendData(this.state.submitValue);

	}

	render() {

	    return (
	      	<div>
	       		<form onSubmit={this.onFormSubmit}>
	        		<div className="board-row">
	        			<CustomButton value='RFI' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton = ref)} ></CustomButton>
	       				<CustomButton value='Vs Open' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton2 = ref)} ></CustomButton>
	       				<CustomButton value='Vs 3' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton3 = ref)} ></CustomButton>
	       				<CustomButton value='BVB' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton4 = ref)} ></CustomButton>
	        		</div>
	        		<input type='submit' />
	        	</form>
	      </div>
	    );
  	}
}


export default PotType;
