import React from 'react';
//import ReactDOM from 'react-dom';
//import Button from '@material-ui/core/Button';

import PotType from './pottype';
import StackDepth from './stackDepth';
import Hero from './hero';
import Villain from './villain';
import BvB from './bvb';


export default class BumFuck extends React.Component {

	constructor() {
        super();

        this.state = {
        	value: null,    
        }

        // this.getData = this.getData.bind(this);   
    }

    // setState forces rerender so buttons update with each click
    componentDidUpdate(val) {
    	console.log(this.state.value)
  		if (this.state.value !== val) {
    		this.setState ({value: val})
	  	}
 //    getData(val) {

	}

	// helper function called in every class's handleClick, allowing user to toggle between buttons 
	// mappy = map of values to buttons (defined in handleclick)
	// ev = event state 
	buttonState(ev, mappy) {
		for (var key in mappy) {
			let button = mappy[key]
			//console.log("VALUE: ", ev.currentTarget.value)
			//console.log("KEY: ", key)
			if (key === ev.currentTarget.value) {
				//console.log("BUTTON: ", mappy[key])
				button.clickedOn()
		 	} 
		 	else {
		 		button.clickedOff()
		 	}
		}	
	}



	render() {
		// result = this.state.value is passing in the result of the potttype decision
		return (
			<div>
				<PotType pButtonState={this.buttonState} pSendData={this.componentDidUpdate}/>
				<StackDepth sButtonState={this.buttonState} result = {this.state.value}/>
				<Hero hButtonState={this.buttonState} result = {this.state.value}/>
				<Villain vButtonState={this.buttonState} result = {this.state.value}/>
				<BvB bButtonState={this.buttonState} result = {this.state.value}/>
			</div>
		);
	}
}
