import React from 'react';
import ReactDOM from 'react-dom';
import Button from '@material-ui/core/Button';
import { createMuiTheme, MuiThemeProvider } from '@material-ui/core/styles';
//import createMuiTheme from 'material-ui/styles/theme'
import {pink, lightBlue} from '@material-ui/core/colors'
// import createPalette from '@material-ui/core/palette'


class Test extends React.Component {

	muiTheme = createMuiTheme({ palette: { primary: lightBlue, secondary: pink } })

	constructor(){
        super();

        this.state = {
        	color: 'lightBlue',
        	clicked: false,
        	button1: undefined
        }
    }





	onButtonClick = (evt) => { //evt = event object, which has an attribute, target, that is a reference to the button that the user clicked
    	const btn = evt.target;
   		console.log(`The user clicked ${btn.name}: ${btn.value}`);
	};


	handleClick = ev => { 
		if (this.state.clicked === false) {
			this.setState({color: 'pink'}) 
			this.setState({clicked: true})

			const newState = {
        		button1 : ev.currentTarget.value
    		};
    		this.setState(newState);
			console.log('hiii')
		}
		if (this.state.clicked === true) {
			this.setState({color: 'lightBlue'}) 
			this.setState({clicked: false})

			const newState = {
        		name: undefined
    		};
    		this.setState(newState);
			console.log('haaaay')
		}

	}


	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    console.log(this.state);
  	};


	render() {
		return (
	      	<div>
		        <h1>Sign Up Sheet</h1>



		        <form onSubmit={this.onFormSubmit}>

		        	<Button
		        		variant="contained" 
		          		name='button1'
		         		value='great'
		         		style={{backgroundColor: this.state.color}}
		         		onClick={() => this.handleClick()}
	          		>
		          		Great
		        	</Button>

			        <Button
			        	variant="contained"
						name='button-2'
						value='amazing'
						style={{backgroundColor: this.state.color}}
		         		onClick={() => this.handleClick()}
			        >
			        	Amazing
		        	</Button>


		          	<input
		            	placeholder='Hole Card'
		            	ref='holeCard'
		          	/>

		          	<input type='submit' />
		        </form>
	      	</div>
	    );

	 }

