import React from 'react';
//import ReactDOM from 'react-dom';
import Button from '@material-ui/core/Button';
import './index.css';

class SubmitButton extends React.Component {

    constructor() {
        super();

        this.state = {
           color: '#002984',
           disabled: true,
        }
    }

    componentDidUpdate(props) {
      let submitButton = this._submitButton;

      if (this.props.holeInputValue.match(/^([AKQJakqj2-9]|10)([AKQJakqj2-9]|10)([osOS])$/)) {
         //this.setState({disabled: false});
         console.log("true bish")
         submitButton.enabled()
      }
      else {
          this.setState({disabled: true});
          console.log("flakse")
          submitButton.disabled()
       }
    }

    disabled() {
        this.setState({disabled: true})
        this.setState({color: '#564D8F'})
     }

     enabled() {
        this.setState({disabled: false})
     }


    // display individual button
    render() {
      const mystyle = {
         backgroundColor: this.state.color,
         width: "15%"
      };
  		return (
            <Button
      			value = "Submit"
      			style = {mystyle}
               disabled = {this.state.disabled}
      			onClick = {this.props.handleSubmit}
               ref = {ref => (this._submitButton = ref)}
      			>
	  		Submit
  			</Button>
        )
  	 }
}

export default SubmitButton;
