import React from 'react';
//import ReactDOM from 'react-dom';
import CustomButton from './customButton';
import './index.css';
import {VS_OPEN, RFI, VS_3, BVB} from './stringConstants';


class PotType extends React.Component {

	constructor() {
		super();

		this.state = {
			submitValue : ''
		}
	}

	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    return(this.state.submitValue);
  	};


	handleClick = ev => { 
		let mappy = {[VS_OPEN]: this._custombutton2, RFI: this._custombutton, VS_3: this._custombutton3, BVB: this._custombutton4}
		this.props.pButtonState(ev, mappy)

		this.setState ({submitValue: ev.currentTarget.value})
		this.props.pSendData(this.state.submitValue)
	}

	render() {

	    return (
	      	<div>
	       		<form onSubmit={this.onFormSubmit}>
	        		<div className="board-row">
	        			<CustomButton value={RFI} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton = ref)} ></CustomButton>
	       				<CustomButton value={VS_OPEN} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton2 = ref)} ></CustomButton>
	       				<CustomButton value={VS_3}onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton3 = ref)} ></CustomButton>
	       				<CustomButton value={BVB} onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton4 = ref)} ></CustomButton>
	        		</div>
	        		<input type='submit' />
	        	</form>
	      </div>
	    );
  	}
}


export default PotType;
