import React from 'react';
import ReactDOM from 'react-dom';
import CustomButton from './customButton';
import './index.css';
import {RFI, VS_OPEN, VS_3, } from './stringConstants';


class PotType extends React.Component {

	constructor() {
		super();

		this.state = {
			submitValue : ''
		}
	}

	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    return(this.state.submitValue);
  	};




	handleClick = ev => { 
		let mappy = {RFI: this._custombutton, VS_OPEN: this._custombutton2, VS_3:  this._custombutton3, BVB: this._custombutton4}
		this.props.pButtonState(ev, mappy)

		this.state.submitValue = ev.currentTarget.value

		this.props.sendData(this.state.submitValue)
	}

	render() {

	    return (
	      	<div>
	       		<form onSubmit={this.onFormSubmit}>
	        		<div className="board-row">
	        			<CustomButton value='RFI' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton = ref)} ></CustomButton>
	       				<CustomButton value='Vs Open' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton2 = ref)} ></CustomButton>
	       				<CustomButton value='Vs 3' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton3 = ref)} ></CustomButton>
	       				<CustomButton value='BvB' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton4 = ref)} ></CustomButton>
	        		</div>
	        		<input type='submit' />
	        	</form>
	      </div>
	    );
  	}
}


export default PotType;
