import React from 'react';
import ReactDOM from 'react-dom';
import CustomButton from './customButton';
import './index.css';
import {UTG, MP, LJ, HJ, CO, BU, SB, BB} from './stringConstants';


class Hero extends React.Component {

	constructor() {
		super();

		this.state = {
			submitValue : ''
		}
	}

	onFormSubmit = (evt) => {
	    evt.preventDefault();
	    console.log(this.state.submitValue);
  	};

	handleClick = ev => { 
		let mappy = {UTG: this._custombutton, MP: this._custombutton2, LJ:  this._custombutton3, HJ: this._custombutton4, 
			CO: this._custombutton5, BU: this._custombutton6, SB:  this._custombutton7, BB: this._custombutton8}
		this.props.vButtonState(ev, mappy)

		this.state.submitValue = ev.currentTarget.value
		this.props.sendData(this.state.submitValue)


		if (ev.currentTarget.value === 'UTG') {
			this._custombutton.clickedOn()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this._custombutton6.clickedOff()
			this._custombutton7.clickedOff()
			this._custombutton8.clickedOff()
			this.state.submitValue = 'UTG'
		}
		else if (ev.currentTarget.value === 'MP') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOn()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this._custombutton6.clickedOff()
			this._custombutton7.clickedOff()
			this._custombutton8.clickedOff()
			this.state.submitValue = 'MP'
		}
		else if (ev.currentTarget.value === 'LJ') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOn()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this._custombutton6.clickedOff()
			this._custombutton7.clickedOff()
			this._custombutton8.clickedOff()
			this.state.submitValue = 'LJ'
		}
		else if (ev.currentTarget.value === 'HJ') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOn()
			this._custombutton5.clickedOff()
			this._custombutton6.clickedOff()
			this._custombutton7.clickedOff()
			this._custombutton8.clickedOff()
			this.state.submitValue = 'HJ'
		}
		else if (ev.currentTarget.value === 'CO') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOn()
			this._custombutton6.clickedOff()
			this._custombutton7.clickedOff()
			this._custombutton8.clickedOff()
			this.state.submitValue = 'CO'
		}
		else if (ev.currentTarget.value === 'BU') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this._custombutton6.clickedOn()
			this._custombutton7.clickedOff()
			this._custombutton8.clickedOff()
			this.state.submitValue = 'BU'
		}
		else if (ev.currentTarget.value === 'SB') {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this._custombutton6.clickedOff()
			this._custombutton7.clickedOn()
			this._custombutton8.clickedOff()
			this.state.submitValue = 'SB'
		}
		else {
			this._custombutton.clickedOff()
			this._custombutton2.clickedOff()
			this._custombutton3.clickedOff()
			this._custombutton4.clickedOff()
			this._custombutton5.clickedOff()
			this._custombutton6.clickedOff()
			this._custombutton7.clickedOff()
			this._custombutton8.clickedOn()
			this.state.submitValue = 'BB'
		}

	}

	render() {

	    return (
	      	<div>
	       		<form onSubmit={this.onFormSubmit}>
	        		<div className="board-row">
			        	<CustomButton value='UTG' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton = ref)} ></CustomButton>
			       		<CustomButton value='MP' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton2 = ref)} ></CustomButton>
			       		<CustomButton value='LJ' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton3 = ref)} ></CustomButton>
			       		<CustomButton value='HJ' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton4 = ref)} ></CustomButton>
			       		<CustomButton value='CO' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton5 = ref)} ></CustomButton>
			       		<CustomButton value='BU' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton6 = ref)} ></CustomButton>
			       		<CustomButton value='SB' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton7 = ref)} ></CustomButton>
			       		<CustomButton value='BB' onClick={(ev) => this.handleClick(ev)} ref = {ref => (this._custombutton8 = ref)} ></CustomButton>
	        		</div>
       		 		<input type='submit' />
	        	</form>
	      	</div>
	    );
	}
}


export default Hero;
